var link=document.querySelector(".search-hotel-button"),popup=document.querySelector(".search-form");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("modal-show"),form.classList.remove("modal-error")});var form=document.querySelector(".search-form-wrapper"),arrival=popup.querySelector("[name=arrival]"),departure=popup.querySelector("[name=departure]"),calcAdults=popup.querySelector("[name=adults]"),calcChildren=popup.querySelector("[name=children]");popup.addEventListener("submit",function(e){arrival.value&&departure.value&&calcAdults.value&&calcChildren.value||(e.preventDefault(),form.classList.remove("modal-error"),form.offsetWidth=popup.offsetWidth,form.classList.add("modal-error"))});